const map = {
	126: '用户不存在',
	139: '用户名只能以英文字母、数字或下划线组成',
	200: '用户名不能为空',
	201: '密码不能为空',
    202: '用户名已存在',
    203: '邮箱已存在',
    204: '电子邮件地址不能为空',
    210: '用户名或密码错误',
    211: '该用户名不存在',
    217: '无效的用户名',
    218: '密码不符合格式',
    219: '登录次数超过限制，请15分钟再试',
    unkown: '未知错误，稍后重试',
}

export default function({code}){
    if (map[code]){
        return map[code];
    } else {
        return map.unknown;
    }
};